import React, { Component } from "react";
import { Link } from "react-router-dom";
import { MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavItem, MDBNavbarToggler, MDBCollapse, MDBFormInline } from "mdbreact";
class Navbar extends Component {

  state = {
    modal5: false,

  }


  //popops login fonction 
  toggleCollapse = collapseID => () =>
    this.setState(prevState => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : ""
    }));
  toggle = nr => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber]
    });
  }


  render() {
    let imgUrl = 'http://www.leparisien.fr/resizer/OHgkvKFl5Mm_hjOsnkhgPnQb6JU=/932x582/arc-anglerfish-eu-central-1-prod-leparisien.s3.amazonaws.com/public/NNRFOUDC2KR3NWBCMVGTYNM2SY.jpg';

    return (
      <div>
        <MDBNavbar color="primary-color-dark" dark expand="md" style={{ marginTop: "1px" }} className="" scrolling >
          <MDBNavbarBrand>
            Ag-Pub
          </MDBNavbarBrand>
          <MDBNavbarToggler onClick={this.toggleCollapse("navbarCollapse3")} />
          <MDBCollapse id="navbarCollapse3" isOpen={this.state.collapseID} navbar>
          <MDBNavbarNav right>
              <MDBNavItem>
                <Link to="/" className="nav-header">Accueil</Link>
              </MDBNavItem>
              <MDBNavItem>
                <Link to="/tous" className="nav-header">Publiciter</Link>
              </MDBNavItem>

              <MDBNavItem>
                <Link to="/register" className="nav-header" >Creer compte</Link>
              </MDBNavItem>
              <MDBNavItem>
                <Link to="/login" className="nav-header" >Connexion</Link>
              </MDBNavItem>
            </MDBNavbarNav>
          </MDBCollapse>
        </MDBNavbar>
        <div className="card card-image" id="header" style={{backgroundImage: 'url(' + imgUrl + ')', 
                                                          backgroundSize: 'cover', backgroundPosition: 'center center',backgroundRepeat: 'no-repeat',}}>
          <div className="text-white text-center rgba-stylish-strong py-5 px-4">
            <div className="py-5">
              <h2 id="h2accueil">VOUS ETES PASSIONNÉ DE CUISINE ?</h2>
              <p className="mb-4 pb-2 px-md-5 mx-md-5">
              Nous sommes <span id="spanheader">un centre de formation de cuisine</span>
              <br/> qui propose des ateliers à nos élèves à partir de 12 ans,<br/>
               mais aussi à des gens particuliers.<br/><br/>
               Des personnes qui veulent apprendre à  cuisiner afin de manger correctement.
              </p>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Navbar;
